public class CheckIfTheNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	//	int num1 =10000;
		int num1;
		num1 = 0; //run1 num1 =35, run2 num1 = -35 and run3 num1 = 0
	//	int num2 = -1;
		
		if(num1 > 0) {
			System.out.println(num1+" Number is Positive");
		}
		
		else if(num1 < 0) {
			System.out.println(num1+" Number is Negative");
		}
		
		else {
			System.out.println(num1 + " Number is neither positive or Negative");
		}
	}
}
